mapper.select:

		call WEBOBJLIB.MWC001MR(#{I_COR,		mode=IN,		jdbcType=VARCHAR}
						,#{I_UID,		mode=IN,		jdbcType=VARCHAR}
						,#{I_IP,		mode=IN,		jdbcType=VARCHAR}
						,#{O_MSGCOD,	mode=OUT,		jdbcType=VARCHAR}
						,#{O_ERRCOD,	mode=OUT,		jdbcType=VARCHAR}
						,#{I_HOS,	mode=IN,	jdbcType=VARCHAR}
						,#{I_CHN,	mode=IN,	jdbcType=VARCHAR}
						,#{I_NAM,	mode=IN,	jdbcType=VARCHAR}
                        ,#{I_SEX,	mode=IN,	jdbcType=VARCHAR}
					  )

mapper.insert:

call WEBOBJLIB.MWC001MC(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                        , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                        , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                        , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_CHN,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_NAM,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_HPN,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_BDT,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_SEX,	mode=IN,	jdbcType=VARCHAR}
                        , #{I_AGE,	mode=IN,	jdbcType=DECIMAL}                                                                                          
                        )

mapper.delete:

call WEBOBJLIB.MWC001MD(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_CHN,	mode=IN,	jdbcType=VARCHAR}
                      )

mapper.update:

call WEBOBJLIB.MWC001MU(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_CHN,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_NAM,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_HPN,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_BDT,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_SEX,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_AGE,	mode=IN,	jdbcType=DECIMAL}                                                                                          
                      )

mapper.select:

call WEBOBJLIB.MWC001MCC(#{I_COR,		mode=IN,		jdbcType=VARCHAR}
						,#{I_UID,		mode=IN,		jdbcType=VARCHAR}
						,#{I_IP,		mode=IN,		jdbcType=VARCHAR}
						,#{O_MSGCOD,	mode=OUT,		jdbcType=VARCHAR}
						,#{O_ERRCOD,	mode=OUT,		jdbcType=VARCHAR}
						,#{I_HOS,		mode=IN,		jdbcType=VARCHAR}
			  )
