mapper.select:

			call WEBOBJLIB.MWC002MR(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                              , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                              , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_NAM,	mode=IN,	jdbcType=VARCHAR}
                              )

mapper.insert:

        call WEBOBJLIB.MWC002MC(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                              , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                              , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_NAM,	mode=IN,	jdbcType=VARCHAR}
							  , #{I_MSG,	mode=IN,	jdbcType=VARCHAR}
                              )

mapper.update:

call WEBOBJLIB.MWC002MU(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_SEQ,	mode=IN,	jdbcType=DECIMAL}
                      , #{I_NAM,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_MSG,	mode=IN,	jdbcType=VARCHAR}                                                                                
                      )


call WEBOBJLIB.MWC002MD(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_SEQ,	mode=IN,	jdbcType=DECIMAL}                                                                             
                      )
