mapper.select:

			call WEBOBJLIB.MWS015R1(
								#{I_COR,    mode=IN, jdbcType=VARCHAR}
                              , #{I_UID,    mode=IN, jdbcType=VARCHAR}
                              , #{I_IP,     mode=IN, jdbcType=VARCHAR}	
							  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
                              , #{I_DCL,	mode=IN, jdbcType=VARCHAR}
                              , #{I_TIT,	mode=IN, jdbcType=VARCHAR}
                              , #{I_CON,	mode=IN, jdbcType=VARCHAR}
                              , #{I_PDNOC,	mode=IN, jdbcType=VARCHAR}
                              , #{I_FWDT,	mode=IN,  jdbcType=DECIMAL}
                              , #{I_TWDT,	mode=IN,  jdbcType=DECIMAL}				 
			)


			call WEBOBJLIB.MWS015R3(
									#{I_COR, mode=IN, jdbcType=VARCHAR}
                              	  , #{I_UID, mode=IN, jdbcType=VARCHAR}
                              	  , #{I_IP, mode=IN, jdbcType=VARCHAR}	
								  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              	  , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
                              	  , #{I_SEQ, mode=IN, jdbcType=DECIMAL}				 
			)

mapper.insert:

			call WEBOBJLIB.MWS015MC(
									#{I_COR,    mode=IN,  jdbcType=VARCHAR}
								  , #{I_UID,    mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_IP,     mode=IN,  jdbcType=VARCHAR}
								  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              	  , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
							  	  , #{I_SEQ,    mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_DCL,    mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_PDNO,   mode=IN,  jdbcType=VARCHAR}
								  , #{I_TIT,    mode=IN,  jdbcType=VARCHAR}
								  , #{I_CON,    mode=IN,  javaType=java.lang.String}
								  , #{I_CNM,    mode=IN,  jdbcType=VARCHAR}
								  , #{I_CMA,    mode=IN,  jdbcType=VARCHAR}
								  , #{I_IYN,    mode=IN,  jdbcType=VARCHAR}
								  , #{I_RCT,    mode=IN,  jdbcType=DECIMAL}
			)


			call WEBOBJLIB.MWS015DC(
									#{I_COR,  mode=IN, jdbcType=VARCHAR}
							  	  , #{I_SEQ,  mode=IN, jdbcType=DECIMAL}
							  	  , #{I_FSQ,  mode=IN, jdbcType=DECIMAL}
							  	  , #{I_FPT,  mode=IN, jdbcType=VARCHAR}
								  , #{I_FNM,  mode=IN, jdbcType=VARCHAR}
								  , #{I_FSNM, mode=IN, jdbcType=VARCHAR}
								  , #{I_UID,  mode=IN, jdbcType=VARCHAR}
							  	  , #{I_IP,   mode=IN, jdbcType=VARCHAR}
								  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              	  , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}				 
			)

mapper.select:

			call WEBOBJLIB.MWS015MS1(
								#{I_COR, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_UID, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_IP, 	mode=IN,  jdbcType=VARCHAR}
							  ,	#{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
		)

