                              , #{I_PDNO, mode=IN, jdbcType=VARCHAR}
                              , #{I_TIT,  mode=IN, jdbcType=VARCHAR}
                              , #{I_CON,  mode=IN, javaType=java.lang.String}
                              , #{I_CNM,  mode=IN, jdbcType=VARCHAR}
