mapper.select:

		call WEBOBJLIB.MWS011R(#{I_COR,		mode=IN,	jdbcType=VARCHAR}
							  , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
							  , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
							  , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
							  , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                              , #{I_RFM,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_RCL,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_RTP,	mode=IN,	jdbcType=VARCHAR}
							  )

mapper.insert:

call WEBOBJLIB.MWS011CC(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_RFM,	mode=IN,	jdbcType=VARCHAR}
                      )


call WEBOBJLIB.MWS011C(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_RFM,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_RCL,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_RTP,	mode=IN,	jdbcType=VARCHAR}
                      )

