mapper.select:

call WEBOBJLIB.MWS013R( 
						#{I_COR, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_UID, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_IP, 		mode=IN,  		jdbcType=VARCHAR}
					  ,	#{O_MSGCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{O_ERRCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{I_MCD, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_MNM, 		mode=IN,  		jdbcType=VARCHAR}
					  )


call WEBOBJLIB.MWS013R2( 
						#{I_COR, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_UID, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_IP, 		mode=IN,  		jdbcType=VARCHAR}
					  ,	#{O_MSGCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{O_ERRCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{I_MCD, 		mode=IN,  		jdbcType=VARCHAR}
					  )


call WEBOBJLIB.MWS013DT( #{I_COR, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_UID, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{I_IP, 		mode=IN,  		jdbcType=VARCHAR}
					  , #{O_MSGCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{O_ERRCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  )


call WEBOBJLIB.MWS013CC( #{I_COR, 		mode=IN,  		jdbcType=VARCHAR}
					   , #{I_UID, 		mode=IN,  		jdbcType=VARCHAR}
					   , #{I_IP, 		mode=IN,  		jdbcType=VARCHAR}
					   , #{O_MSGCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					   , #{O_ERRCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					   , #{I_MCD, 		mode=IN,  		jdbcType=VARCHAR}
					   )

mapper.insert:

call WEBOBJLIB.MWS013C( #{I_COR, 		mode=IN, 		jdbcType=VARCHAR}
					  , #{I_UID, 		mode=IN, 		jdbcType=VARCHAR}
					  , #{I_IP, 		mode=IN, 		jdbcType=VARCHAR}
					  , #{O_MSGCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{O_ERRCOD, 	mode=OUT, 		jdbcType=VARCHAR}
					  , #{I_MCD, 		mode=IN, 		jdbcType=VARCHAR}
					  , #{I_CON, 		mode=IN, 		jdbcType=VARCHAR}
					  )

mapper.delete:
		call WEBOBJLIB.MWS013D( 
								 #{I_COR, 		mode=IN, 		jdbcType=VARCHAR}
								,#{I_UID,  		mode=IN, 		jdbcType=VARCHAR}
                                ,#{I_IP,   		mode=IN, 		jdbcType=VARCHAR}
								,#{O_MSGCOD,	mode=OUT,		jdbcType=VARCHAR}
								,#{O_ERRCOD,	mode=OUT,		jdbcType=VARCHAR}
								,#{I_MCD, 		mode=IN, 		jdbcType=VARCHAR}
							   )
