mapper.select:

		call WEBOBJLIB.MWS012R1(#{I_COR,		mode=IN,	jdbcType=VARCHAR}
							  , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
							  , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
							  , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
							  , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                              , #{I_PCD,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_FNM,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_MBC,	mode=IN,	jdbcType=VARCHAR}
                              , #{I_NAM,	mode=IN,	jdbcType=VARCHAR}
							  )


call WEBOBJLIB.MWS012R2(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_RCL,	mode=IN,	jdbcType=VARCHAR}
                      )

mapper.insert:

call WEBOBJLIB.MWS012C(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_RCL,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_NSQ,	mode=IN,	jdbcType=DECIMAL}
                      , #{I_NCL,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_NDV,	mode=IN,	jdbcType=VARCHAR}
                      )

mapper.update:

call WEBOBJLIB.MWS012U(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_RCL,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_NSQ,	mode=IN,	jdbcType=DECIMAL}
                      , #{I_NCL,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_NDV,	mode=IN,	jdbcType=VARCHAR}
                      )

mapper.delete:

call WEBOBJLIB.MWS012D(#{I_COR,		mode=IN,	jdbcType=VARCHAR}
                      , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
                      , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
                      , #{I_HOS,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_RCL,	mode=IN,	jdbcType=VARCHAR}
                      , #{I_NSQ,	mode=IN,	jdbcType=DECIMAL}
                      )
