mapper.select:

call WEBOBJLIB.MWS007R1(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
					  , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
					  , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
					  , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
					  , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
					  , #{I_GRDMCD,	mode=IN,	jdbcType=VARCHAR}
					  )


		call WEBOBJLIB.MWS007CC(#{I_COR,	mode=IN,	jdbcType=VARCHAR}
							  , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
							  , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
