mapper.update:

call  WEBOBJLIB.MWR001U2(#{I_COR, 	mode=IN, 	jdbcType=VARCHAR}
					  , #{I_UID, 	mode=IN, 	jdbcType=VARCHAR}
					  , #{I_IP, 	mode=IN, 	jdbcType=VARCHAR}
					  , #{O_MSGCOD, mode=OUT, 	jdbcType=VARCHAR}
					  , #{O_ERRCOD, mode=OUT, 	jdbcType=VARCHAR}
					  , #{I_DAT,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_JNO,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_GCD,	mode=IN,	jdbcType=CHAR}
					  , #{I_STS,	mode=IN,	jdbcType=VARCHAR}
					  , #{I_TCD,	mode=IN,	jdbcType=VARCHAR}
					  , #{I_REJ,	mode=IN,	jdbcType=VARCHAR}
					  , #{I_SSU,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_BSU,	mode=IN,	jdbcType=DECIMAL}
				 	  , #{I_JSU,	mode=IN,	jdbcType=DECIMAL}
				 	  , #{I_ASU,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_BCC,	mode=IN,	jdbcType=CHAR}
					  , #{I_CSU,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_CCR,	mode=IN,	jdbcType=DECIMAL}
					  )

mapper.select:

call WEBOBJLIB.MWR001CC(#{I_COR,		mode=IN,	jdbcType=VARCHAR}
					  , #{I_UID,	mode=IN,	jdbcType=VARCHAR}
					  , #{I_IP,		mode=IN,	jdbcType=VARCHAR}
					  , #{O_MSGCOD,	mode=OUT,	jdbcType=VARCHAR}
					  , #{O_ERRCOD,	mode=OUT,	jdbcType=VARCHAR}
					  , #{I_DAT,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_JNO,	mode=IN,	jdbcType=DECIMAL}
					  , #{I_GCD,	mode=IN,	jdbcType=CHAR}
					  )


call WEBOBJLIB.MC185RM( 
					   #{I_DAT,	mode=IN,	jdbcType=DECIMAL}
					 , #{I_JNO,	mode=IN,	jdbcType=DECIMAL}
					 , #{I_GCD,	mode=IN,	jdbcType=CHAR}
