					 , #{O_BSU,	mode=OUT,	jdbcType=DECIMAL}
					 , #{O_JSU,	mode=OUT,	jdbcType=DECIMAL}
					 , #{O_ASU,	mode=OUT,	jdbcType=DECIMAL}
					 , #{O_DCR,	mode=OUT,	jdbcType=DECIMAL}
					 , #{O_BCC,	mode=OUT,	jdbcType=CHAR}
					 , #{O_ERR,	mode=OUT,	jdbcType=CHAR}
					 , #{I_COR,	mode=IN,	jdbcType=CHAR}
					 , #{O_CSU,	mode=OUT,	jdbcType=DECIMAL}
					 , #{O_CCR,	mode=OUT,	jdbcType=DECIMAL}
