mapper.select:

call WEBOBJLIB.MWS001R1(#{I_COR, mode=IN, jdbcType=VARCHAR}
					  , #{I_UID, mode=IN, jdbcType=VARCHAR}
					  , #{I_IP, mode=IN, jdbcType=VARCHAR}
					  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{O_ERRCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{I_MCD, mode=IN, jdbcType=VARCHAR}
					  , #{I_MNM, mode=IN, jdbcType=VARCHAR}

					  )


call WEBOBJLIB.MWS001R3(#{I_COR, mode=IN, jdbcType=VARCHAR}
					  , #{I_UID, mode=IN, jdbcType=VARCHAR}
					  , #{I_IP, mode=IN, jdbcType=VARCHAR}
					  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{O_ERRCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{I_AGR, mode=IN, jdbcType=VARCHAR}
					  )


call WEBOBJLIB.MWS001R2(#{I_COR, mode=IN, jdbcType=VARCHAR}
					  , #{I_UID, mode=IN, jdbcType=VARCHAR}
					  , #{I_IP, mode=IN, jdbcType=VARCHAR}
					  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{O_ERRCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{I_PMCD, mode=IN, jdbcType=VARCHAR}

					  )


call WEBOBJLIB.MWS001CC(#{I_COR, mode=IN, jdbcType=VARCHAR}
					  , #{I_UID, mode=IN, jdbcType=VARCHAR}
					  , #{I_IP, mode=IN, jdbcType=VARCHAR}
					  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{O_ERRCOD, mode=OUT, jdbcType=VARCHAR}
					  , #{I_MCD, mode=IN, jdbcType=VARCHAR}
					  , #{I_PMCD, mode=IN, jdbcType=VARCHAR}
					  , #{I_SEQ, mode=IN, jdbcType=DECIMAL}
					  , #{I_MNM, mode=IN, jdbcType=VARCHAR}
					  , #{I_PTH, mode=IN, jdbcType=VARCHAR}
					  , #{I_ICN, mode=IN, jdbcType=VARCHAR}
					  , #{I_UYN, mode=IN, jdbcType=VARCHAR}

					  )

