mapper.select:

			call WEBOBJLIB.MWS014R1(	
								#{I_COR, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_UID, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_IP, 	mode=IN,  jdbcType=VARCHAR}	
							  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
                              , #{I_DCL,	mode=IN,  jdbcType=VARCHAR}
                              , #{I_PYN,	mode=IN,  jdbcType=VARCHAR}
                              , #{I_TIT,	mode=IN,  jdbcType=VARCHAR}
                              , #{I_CON,	mode=IN,  jdbcType=VARCHAR}
                              , #{I_FWDT,	mode=IN,  jdbcType=DECIMAL}
                              , #{I_TWDT,	mode=IN,  jdbcType=DECIMAL}
                              , #{I_PFR,	mode=IN,  jdbcType=DECIMAL}
                              , #{I_PTO,	mode=IN,  jdbcType=DECIMAL}
			)

mapper.insert:

			call WEBOBJLIB.MWS014MC(
									#{I_COR,    mode=IN,  jdbcType=VARCHAR}
								  , #{I_UID,    mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_IP,     mode=IN,  jdbcType=VARCHAR}
							  	  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              	  , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
							  	  , #{I_SEQ,    mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_CNM,    mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_DNO,    mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_DCL,  	mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_SDV,  	mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_PYN,  	mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_PFR,  	mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_PTO,  	mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_LCO,  	mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_TCO,  	mode=IN,  jdbcType=DECIMAL}
								  , #{I_TIT,  	mode=IN,  jdbcType=VARCHAR}
								  , #{I_CON,  	mode=IN,  javaType=java.lang.String}
			)

mapper.select:

			call WEBOBJLIB.MWS014MS1(
								#{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}	
		)

mapper.insert:

			call WEBOBJLIB.MWS014DC(
									#{I_COR,  	mode=IN,  jdbcType=VARCHAR}
								  , #{I_UID,  	mode=IN,  jdbcType=VARCHAR}
							  	  , #{I_IP,   	mode=IN,  jdbcType=VARCHAR}
								  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              	  , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
							  	  , #{I_SEQ,  	mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_FSQ,  	mode=IN,  jdbcType=DECIMAL}
							  	  , #{I_FPT,  	mode=IN,  jdbcType=VARCHAR}
								  , #{I_FNM,  	mode=IN,  jdbcType=VARCHAR}
								  , #{I_FSNM, 	mode=IN,  jdbcType=VARCHAR}
			)

mapper.select:

			call WEBOBJLIB.MWS014R2(
									#{I_COR, 	mode=IN,  jdbcType=VARCHAR}
                              	  , #{I_UID, 	mode=IN,  jdbcType=VARCHAR}
                              	  , #{I_IP, 	mode=IN,  jdbcType=VARCHAR}	
								  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              	  , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
                              	  , #{I_SEQ, 	mode=IN,  jdbcType=DECIMAL}				 
			)


			call WEBOBJLIB.MWS014DR(
								#{I_COR, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_UID, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_IP, 	mode=IN,  jdbcType=VARCHAR}
							  , #{O_MSGCOD, mode=OUT, jdbcType=VARCHAR}
                              , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
							  ,	#{I_SEQ, 	mode=IN,  jdbcType=DECIMAL}
			)

mapper.delete:
		call WEBOBJLIB.MWS014D( 
								 #{I_COR, 		mode=IN, 		jdbcType=VARCHAR}
								,#{I_UID,  		mode=IN, 		jdbcType=VARCHAR}
                                ,#{I_IP,   		mode=IN, 		jdbcType=VARCHAR}
								,#{O_MSGCOD,	mode=OUT,		jdbcType=VARCHAR}
								,#{O_ERRCOD,	mode=OUT,		jdbcType=VARCHAR}
								,#{S014SEQ,   	mode=IN, 		jdbcType=DECIMAL}
					)

		call WEBOBJLIB.MWS014DD( 
								 #{I_COR, 		mode=IN, 		jdbcType=VARCHAR}
								,#{I_UID,  		mode=IN, 		jdbcType=VARCHAR}
                                ,#{I_IP,   		mode=IN, 		jdbcType=VARCHAR}
								,#{O_MSGCOD,	mode=OUT,		jdbcType=VARCHAR}
								,#{O_ERRCOD,	mode=OUT,		jdbcType=VARCHAR}
								,#{S014SEQ,   	mode=IN, 		jdbcType=DECIMAL}
					)

mapper.update:

			call WEBOBJLIB.MWS014MU(
								#{I_COR,	mode=IN,  jdbcType=VARCHAR}
							  , #{I_UID,  	mode=IN,  jdbcType=VARCHAR}
