                              , #{O_ERRCOD,	mode=OUT, jdbcType=VARCHAR}
							  , #{I_SEQ,  	mode=IN,  jdbcType=DECIMAL}
							  , #{I_CNM,  	mode=IN,  jdbcType=VARCHAR}
							  , #{I_DNO, 	mode=IN,  jdbcType=VARCHAR}
                              , #{I_DCL,  	mode=IN,  jdbcType=VARCHAR}
                              , #{I_SDV,  	mode=IN,  jdbcType=VARCHAR}
                              , #{I_PYN,  	mode=IN,  jdbcType=VARCHAR}
                              , #{I_PFR,   	mode=IN,  jdbcType=DECIMAL}
                              , #{I_PTO,   	mode=IN,  jdbcType=DECIMAL}
                              , #{I_LCO,  	mode=IN,  jdbcType=DECIMAL}
							  , #{I_TCO,  	mode=IN,  jdbcType=DECIMAL}
                              , #{I_TIT,  	mode=IN,  jdbcType=VARCHAR}
                              , #{I_CON,  	mode=IN,  javaType=java.lang.String}
			)


			call WEBOBJLIB.MWS014DU(
								#{I_COR,	mode=IN,  jdbcType=VARCHAR}
							  , #{I_UID,  	mode=IN,  jdbcType=VARCHAR}
                              , #{I_IP,   	mode=IN,  jdbcType=VARCHAR}
